import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import MobileTabBar from './MobileTabBar';
import { ErrorBoundary } from './common/ErrorBoundary';

const VenuePage: React.FC = () => {
  const [code, setCode] = useState('');
  const [activeTab, setActiveTab] = useState('Главная');
  const navigate = useNavigate();
  const { venueId } = useParams<{ venueId: string }>();

  // Данные заведений
  const venues = {
    '1': {
      id: 1,
      name: 'Пиццерия «Мама Мия»',
      address: 'ул. Ленина, 15',
      points: '5 баллов',
      logo: '/assets/brands/mamamia.svg'
    },
    '2': {
      id: 2,
      name: 'Кофейня «Бодрость»',
      address: 'пр. Мира, 42',
      points: '3 балла',
      logo: '/assets/brands/bodrost.svg'
    },
    '3': {
      id: 3,
      name: 'Бургерная «Вкусно»',
      address: 'ул. Гагарина, 8',
      points: '4 балла',
      logo: '/assets/brands/vkusno.svg'
    },
    '4': {
      id: 4,
      name: 'Ресторан «Золотой Дракон»',
      address: 'пр. Победы, 25',
      points: '6 баллов',
      logo: '/assets/brands/mamamia.svg'
    },
    '5': {
      id: 5,
      name: 'Кафе «Уют»',
      address: 'ул. Центральная, 7',
      points: '4 балла',
      logo: '/assets/brands/bodrost.svg'
    },
    '6': {
      id: 6,
      name: 'Столовая «Сытно»',
      address: 'ул. Рабочая, 12',
      points: '3 балла',
      logo: '/assets/brands/vkusno.svg'
    },
    '7': {
      id: 7,
      name: 'Пекарня «Свежий хлеб»',
      address: 'ул. Хлебная, 3',
      points: '2 балла',
      logo: '/assets/brands/mamamia.svg'
    }
  };

  const venue = venues[venueId as keyof typeof venues];

  if (!venue) {
    return (
      <ErrorBoundary>
        <div className="container">
          <div className="card">
            <h3>Заведение не найдено</h3>
            <button onClick={() => navigate('/places')} className="btn">
              Вернуться к списку заведений
            </button>
          </div>
        </div>
      </ErrorBoundary>
    );
  }

  const handleCodeSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (code.length === 6) {
      try {
        // Здесь будет логика отправки кода для конкретного заведения
        alert(`Код успешно использован в ${venue.name}! Получено ${venue.points}`);
        setCode('');
      } catch (error) {
        console.error('Ошибка использования кода:', error);
      }
    }
  };

  const handleTabClick = (tab: string) => {
    if (tab === 'Главная') {
      navigate('/');
    }
  };

  const handleActionClick = (action: string) => {
    console.log('Действие:', action);
    // Здесь будет логика для каждого действия
    if (action === 'exchange') {
      navigate(`/venue/${venueId}/exchange`);
    }
  };

  // Определяем, является ли устройство мобильным
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);

    // Добавляем класс для мобильной версии
    if (isMobile) {
      document.body.classList.add('mobile-layout');
    } else {
      document.body.classList.remove('mobile-layout');
    }

    return () => {
      window.removeEventListener('resize', checkMobile);
      document.body.classList.remove('mobile-layout');
    };
  }, [isMobile]);

  return (
    <ErrorBoundary>
      <div className="container" style={{height: '100%', padding: 0}}>
        <div className="card full-height-card" style={{borderRadius: '38px'}}>
          <div style={{padding: '20px 20px 0 20px', borderBottom: '1px solid var(--border-color)', marginBottom: '20px'}}>
            <div style={{display: 'flex', alignItems: 'center', gap: '12px'}}>
              <button 
                onClick={() => navigate('/places')}
                style={{
                  background: 'transparent',
                  border: 'none',
                  color: 'var(--text-color)',
                  fontSize: '24px',
                  cursor: 'pointer',
                  padding: '8px',
                  borderRadius: '50%',
                  transition: 'background-color 0.2s ease'
                }}
                onMouseEnter={(e) => e.currentTarget.style.backgroundColor = 'rgba(0,123,255,0.1)'}
                onMouseLeave={(e) => e.currentTarget.style.backgroundColor = 'transparent'}
              >
                ←
              </button>
            </div>
          </div>

          <div className="scrollable-content">
            {/* Блок с баллами заведения */}
            <div className="list-item" style={{padding: '10px', marginBottom: '8px'}}>
              <img
                src="/assets/icons/points-1-gold.svg"
                width="24"
                height="24"
                alt="баллы"
              />
              <div>
                <div className="points-text" style={{fontSize: '20px', lineHeight: '1.2'}}>
                  0 баллов
                </div>
                <div className="sub" style={{fontSize: '12px', lineHeight: '1.3'}}>
                  Баланс в {venue.name}
                </div>
              </div>
            </div>

            <div style={{height: '8px'}}></div>

            {/* Форма ввода кода */}
            <form onSubmit={handleCodeSubmit} style={{marginBottom: '8px'}}>
              <div style={{display: 'flex', gap: '16px', marginBottom: '8px', justifyContent: 'center'}}>
                {[0, 1, 2, 3, 4, 5].map((index) => (
                  <input
                    key={index}
                    type="text"
                    maxLength={1}
                    value={code[index] || ''}
                    onChange={(e) => {
                      const newCode = code.split('');
                      newCode[index] = e.target.value.toUpperCase();
                      setCode(newCode.join(''));

                      // Автоматический переход к следующему полю
                      if (e.target.value && index < 5) {
                        const target = e.target as HTMLInputElement;
                        const nextInput = target.parentElement?.children[index + 1] as HTMLInputElement;
                        nextInput?.focus();
                      }
                    }}
                    onKeyDown={(e) => {
                      // Переход к предыдущему полю при нажатии Backspace
                      if (e.key === 'Backspace' && !code[index] && index > 0) {
                        const target = e.target as HTMLInputElement;
                        const prevInput = target.parentElement?.children[index - 1] as HTMLInputElement;
                        prevInput?.focus();
                      }
                    }}
                    style={{
                      width: '45px',
                      height: '45px',
                      fontSize: '19px',
                      textAlign: 'center',
                      border: '2px solid #444',
                      borderRadius: '8px',
                      backgroundColor: '#2a2a2a',
                      color: '#fff',
                      outline: 'none',
                      fontWeight: 'bold',
                      boxSizing: 'border-box'
                    }}
                  />
                ))}
              </div>
              <button 
                type="submit" 
                className="btn"
                disabled={code.length !== 6}
                style={{
                  fontSize: '16px', 
                  padding: '8px 24px', 
                  height: '32px', 
                  lineHeight: '1.2', 
                  width: '100%',
                  marginTop: '12px'
                }}
              >
                Получить
              </button>
            </form>

            {/* Кнопка обмена баллов */}
            <button 
              className="btn"
              onClick={() => handleActionClick('exchange')} 
              style={{
                fontSize: '16px', 
                padding: '8px 24px', 
                height: '32px', 
                lineHeight: '1.2', 
                width: '100%',
                marginTop: '12px',
                marginBottom: '20px'
              }}
            >
              Обменять баллы
            </button>

            <div style={{height: '12px'}}></div>

            {/* Информация о заведении */}
            <div className="list-item">
              <img className="brand" src={venue.logo} alt={venue.name} />
              <div>
                <div style={{fontWeight: 600}}>{venue.name}</div>
                <div className="sub">{venue.address}</div>
              </div>
            </div>

            <div style={{height: '4px'}}></div>

            <div style={{height: '12px'}}></div>
          </div>
        </div>
      </div>
    </ErrorBoundary>
  );
};

export default VenuePage;
