import React, { useState } from 'react';
import Header from './common/Header';
import { ErrorBoundary } from './common/ErrorBoundary';
import { useAuth } from '../contexts/AuthContext';

const VenueAdminPage: React.FC = () => {
  const [venueCode] = useState('VKU'); // По умолчанию

  const { isAuthenticated, user, logout } = useAuth();


  return (
    <ErrorBoundary>
      <div className="container">
        <Header 
          title="Loya Админ" 
          subtitle={`${venueInfo?.name || 'Заведение'} (${venueCode})`}
          user={user}
          onLogout={logout}
        />

        {error && (
          <div className="error">
            {error}
          </div>
        )}

        <div className="admin-content">
          {/* Общая статистика */}
          <StatsOverview 
            stats={statsCards}
            onStatClick={handleStatClick}
          />

          {/* Графики */}
          <div className="charts-section">
            <div className="chart-row">
              <RevenueChart 
                data={revenueData}
                title="Динамика выручки за неделю"
              />
            </div>

            <div className="chart-row">
              <CustomerActivity 
                data={activityData}
                title="Активность клиентов по часам"
              />
            </div>
          </div>

          {/* Дополнительная информация */}
          <div className="info-section">
            <div className="info-card">
              <h4>Информация о заведении</h4>
              <div className="info-grid">
                <div className="info-item">
                  <span className="info-label">Код заведения:</span>
                  <span className="info-value">{venueCode}</span>
                </div>
                <div className="info-item">
                  <span className="info-label">Множитель баллов:</span>
                  <span className="info-value">{venueInfo?.pointsMultiplier || 1}x</span>
                </div>
                <div className="info-item">
                  <span className="info-label">Статус:</span>
                  <span className="info-value">
                    <span className="badge success">Активно</span>
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="admin-actions">
          <button 
            className="btn btn-secondary"
            onClick={logout}
          >
            Выйти
          </button>
        </div>
      </div>
    </ErrorBoundary>
  );
};

export default VenueAdminPage;
