// Сервис для работы с платежами через Юкассу
export interface PaymentData {
  amount: number;
  currency: string;
  description: string;
  returnUrl: string;
  metadata?: Record<string, string>;
}

export interface PaymentResponse {
  id: string;
  status: string;
  confirmation: {
    type: string;
    confirmation_url: string;
  };
}

class PaymentService {
  private readonly corsProxy = 'https://thingproxy.freeboard.io/fetch/';
  private readonly yookassaUrl = 'https://api.yookassa.ru/v3';
  private readonly apiKey = 'test__UW59qNHWI_40gv3XfJTubp_5zQtFKft2UOLRSy8oxI';
  private readonly shopId = '1184633';

  // Создание платежа через CORS-прокси
  async createPayment(paymentData: PaymentData): Promise<PaymentResponse> {
    const idempotenceKey = this.generateIdempotenceKey();
    
    const requestBody = {
      amount: {
        value: paymentData.amount.toFixed(2),
        currency: paymentData.currency || 'RUB'
      },
      capture: true,
      confirmation: {
        type: 'redirect',
        return_url: paymentData.returnUrl
      },
      description: paymentData.description,
      metadata: paymentData.metadata || {}
    };

    try {
      const response = await fetch(`${this.corsProxy}${this.yookassaUrl}/payments`, {
        method: 'POST',
        headers: {
          'Authorization': `Basic ${btoa(`${this.shopId}:${this.apiKey}`)}`,
          'Idempotence-Key': idempotenceKey,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(requestBody)
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();
      
      console.log('Создан платеж через Юкассу:', {
        paymentId: result.id,
        status: result.status,
        confirmationUrl: result.confirmation?.confirmation_url
      });

      return {
        id: result.id,
        status: result.status,
        confirmation: {
          type: result.confirmation.type,
          confirmation_url: result.confirmation.confirmation_url
        }
      };
    } catch (error) {
      console.error('Ошибка создания платежа:', error);
      throw new Error('Не удалось создать платеж. Пожалуйста, попробуйте еще раз.');
    }
  }

  // Получение статуса платежа через CORS-прокси
  async getPaymentStatus(paymentId: string): Promise<any> {
    try {
      const response = await fetch(`${this.corsProxy}${this.yookassaUrl}/payments/${paymentId}`, {
        method: 'GET',
        headers: {
          'Authorization': `Basic ${btoa(`${this.shopId}:${this.apiKey}`)}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();
      
      console.log('Получен статус платежа:', {
        paymentId: result.id,
        status: result.status,
        amount: result.amount,
        paid: result.paid
      });

      return result;
    } catch (error) {
      console.error('Ошибка получения статуса платежа:', error);
      throw new Error('Не удалось получить статус платежа.');
    }
  }

  // Генерация уникального ключа для идемпотентности
  private generateIdempotenceKey(): string {
    return Date.now().toString() + Math.random().toString(36).substr(2, 9);
  }

  // Создание платежа для продления подписки
  async createSubscriptionPayment(months: number = 1): Promise<PaymentResponse> {
    const amount = months * 1000; // 1000 рублей за месяц
    
    return this.createPayment({
      amount,
      currency: 'RUB',
      description: `Продление подписки Loya на ${months} ${months === 1 ? 'месяц' : 'месяца'}`,
      returnUrl: `${window.location.origin}/payment/success`,
      metadata: {
        type: 'subscription_renewal',
        months: months.toString(),
        venue_code: 'VKU'
      }
    });
  }
}

export const paymentService = new PaymentService();
