import React, { useState, useEffect } from 'react';
import { useAnalytics } from '../hooks/useAnalytics';
import StatsOverview from './dashboard/StatsOverview';
import RevenueChart from './analytics/RevenueChart';
import CustomerActivity from './analytics/CustomerActivity';
import Header from './common/Header';
import LoadingSpinner from './common/LoadingSpinner';
import { ErrorBoundary } from './common/ErrorBoundary';
import { useAuth } from '../contexts/AuthContext';

const VenueAdminPage: React.FC = () => {
  const [venueCode] = useState('VKU'); // –ü–æ —É–º–æ–ª—á–∞–Ω–∏—é
  const [venueInfo, setVenueInfo] = useState<any>(null);
  const [stats, setStats] = useState<any>(null);
  const [balance, setBalance] = useState<any>(null);

  const { isAuthenticated, user, logout } = useAuth();
  const { 
    isLoading, 
    error, 
    getVenueInfo, 
    getVenueStats, 
    getVenueBalance,
    getWeeklyReport,
    clearError 
  } = useAnalytics();

  // –ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö –ø—Ä–∏ –º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
  useEffect(() => {
    if (isAuthenticated) {
      loadData();
    }
  }, [isAuthenticated, venueCode]);

  const loadData = async () => {
    try {
      clearError();

      const [info, venueStats, venueBalance] = await Promise.all([
        getVenueInfo(venueCode),
        getVenueStats(venueCode),
        getVenueBalance(venueCode)
      ]);

      setVenueInfo(info);
      setStats(venueStats);
      setBalance(venueBalance);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö:', error);
    }
  };


  const handleStatClick = (statId: string) => {
    console.log('–ö–ª–∏–∫ –ø–æ —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–µ:', statId);
    // –ó–¥–µ—Å—å –±—É–¥–µ—Ç –ª–æ–≥–∏–∫–∞ –¥–ª—è –¥–µ—Ç–∞–ª—å–Ω–æ–≥–æ –ø—Ä–æ—Å–º–æ—Ç—Ä–∞
  };

  // –ú–æ–∫ –¥–∞–Ω–Ω—ã–µ –¥–ª—è –≥—Ä–∞—Ñ–∏–∫–æ–≤
  const revenueData = [
    { date: '2024-01-01', revenue: 50000, points: 1000, customers: 25 },
    { date: '2024-01-02', revenue: 45000, points: 900, customers: 22 },
    { date: '2024-01-03', revenue: 60000, points: 1200, customers: 30 },
    { date: '2024-01-04', revenue: 55000, points: 1100, customers: 28 },
    { date: '2024-01-05', revenue: 70000, points: 1400, customers: 35 },
    { date: '2024-01-06', revenue: 65000, points: 1300, customers: 32 },
    { date: '2024-01-07', revenue: 80000, points: 1600, customers: 40 }
  ];

  const activityData = [
    { hour: '09:00', customers: 5, transactions: 8, revenue: 12000 },
    { hour: '10:00', customers: 8, transactions: 12, revenue: 18000 },
    { hour: '11:00', customers: 12, transactions: 18, revenue: 25000 },
    { hour: '12:00', customers: 20, transactions: 30, revenue: 40000 },
    { hour: '13:00', customers: 25, transactions: 35, revenue: 45000 },
    { hour: '14:00', customers: 18, transactions: 25, revenue: 32000 },
    { hour: '15:00', customers: 15, transactions: 20, revenue: 28000 },
    { hour: '16:00', customers: 10, transactions: 15, revenue: 20000 }
  ];

  // –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –¥–∞–Ω–Ω—ã—Ö –¥–ª—è —Å—Ç–∞—Ç–∏—Å—Ç–∏—á–µ—Å–∫–∏—Ö –∫–∞—Ä—Ç–æ—á–µ–∫
  const statsCards = [
    {
      id: 'revenue',
      label: '–í—ã—Ä—É—á–∫–∞',
      value: balance?.currentBalance || 0,
      change: 12,
      changeType: 'positive' as const,
      icon: 'üí∞'
    },
    {
      id: 'customers',
      label: '–ö–ª–∏–µ–Ω—Ç—ã',
      value: stats?.totalCustomers || 0,
      change: 8,
      changeType: 'positive' as const,
      icon: 'üë•'
    },
    {
      id: 'points',
      label: '–í—ã–¥–∞–Ω–æ –±–∞–ª–ª–æ–≤',
      value: stats?.totalPointsAwarded || 0,
      change: 15,
      changeType: 'positive' as const,
      icon: '‚≠ê'
    },
    {
      id: 'transactions',
      label: '–¢—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏',
      value: stats?.totalCodesGenerated || 0,
      change: -3,
      changeType: 'negative' as const,
      icon: 'üìä'
    }
  ];

  if (isLoading) {
    return <LoadingSpinner text="–ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö..." />;
  }

  return (
    <ErrorBoundary>
      <div className="container">
        <Header 
          title="Loya –ê–¥–º–∏–Ω" 
          subtitle={`${venueInfo?.name || '–ó–∞–≤–µ–¥–µ–Ω–∏–µ'} (${venueCode})`}
          user={user}
          onLogout={logout}
        />

        {error && (
          <div className="error">
            {error}
          </div>
        )}

        <div className="admin-content">
          {/* –û–±—â–∞—è —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ */}
          <StatsOverview 
            stats={statsCards}
            onStatClick={handleStatClick}
          />

          {/* –ì—Ä–∞—Ñ–∏–∫–∏ */}
          <div className="charts-section">
            <div className="chart-row">
              <RevenueChart 
                data={revenueData}
                title="–î–∏–Ω–∞–º–∏–∫–∞ –≤—ã—Ä—É—á–∫–∏ –∑–∞ –Ω–µ–¥–µ–ª—é"
              />
            </div>

            <div className="chart-row">
              <CustomerActivity 
                data={activityData}
                title="–ê–∫—Ç–∏–≤–Ω–æ—Å—Ç—å –∫–ª–∏–µ–Ω—Ç–æ–≤ –ø–æ —á–∞—Å–∞–º"
              />
            </div>
          </div>

          {/* –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è */}
          <div className="info-section">
            <div className="info-card">
              <h4>–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –∑–∞–≤–µ–¥–µ–Ω–∏–∏</h4>
              <div className="info-grid">
                <div className="info-item">
                  <span className="info-label">–ö–æ–¥ –∑–∞–≤–µ–¥–µ–Ω–∏—è:</span>
                  <span className="info-value">{venueCode}</span>
                </div>
                <div className="info-item">
                  <span className="info-label">–ú–Ω–æ–∂–∏—Ç–µ–ª—å –±–∞–ª–ª–æ–≤:</span>
                  <span className="info-value">{venueInfo?.pointsMultiplier || 1}x</span>
                </div>
                <div className="info-item">
                  <span className="info-label">–°—Ç–∞—Ç—É—Å:</span>
                  <span className="info-value">
                    <span className="badge success">–ê–∫—Ç–∏–≤–Ω–æ</span>
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="admin-actions">
          <button 
            className="btn btn-secondary"
            onClick={() => setIsAuthenticated(false)}
          >
            –í—ã–π—Ç–∏
          </button>
        </div>
      </div>
    </ErrorBoundary>
  );
};

export default VenueAdminPage;
