          </div>
          
          {/* –°—Ç–∏–ª–∏ –¥–ª—è –∞–Ω–∏–º–∏—Ä–æ–≤–∞–Ω–Ω–æ–π –≥—Ä–∞–¥–∏–µ–Ω—Ç–Ω–æ–π –æ–±–≤–æ–¥–∫–∏ */}
          <style>{`
            @keyframes qr-border-animation {
              0% {
                background-position: 0% 50%;
              }
              50% {
                background-position: 100% 50%;
              }
              100% {
                background-position: 0% 50%;
              }
            }
            
            .qr-scan-button-wrapper {
              position: relative;
              padding: 2px;
              background: linear-gradient(135deg, #64d8cb, #b39ddb, #64d8cb, #b39ddb);
              background-size: 300% 300%;
              border-radius: var(--radius-lg);
              animation: qr-border-animation 3s ease infinite;
            }
            
            .qr-scan-button {
              position: relative;
              background: var(--glass-bg) !important;
            }
          `}</style>

          {error && (
            <div style={{
              background: 'rgba(239, 68, 68, 0.1)',
              border: '1px solid rgba(239, 68, 68, 0.3)',
              borderRadius: 'var(--radius-md)',
              padding: '12px',
              color: 'var(--error)',
              fontSize: '14px',
              marginTop: '12px'
            }}>
              {error}
            </div>
          )}
        </div>


        {/* –ü–æ—Å–ª–µ–¥–Ω—è—è –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç—å */}
        <div className="modern-card fade-in" style={{ animationDelay: '0.2s' }}>
          <h3 style={{ 
            margin: '0 0 20px 0', 
            color: 'var(--text-primary)',
            fontSize: '18px',
            fontWeight: '600'
          }}>
            –ü–æ—Å–ª–µ–¥–Ω—è—è –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç—å
          </h3>
          
          <div 
            className="hidden-scrollbar"
            style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              gap: '12px',
              maxHeight: '200px',
              overflowY: 'auto'
            }}
          >
            {recentActivity.map((activity, index) => (
              <div 
                key={activity.id}
                className="liquid-glass liquid-glass-hover slide-up"
                style={{ 
                  animationDelay: `${0.3 + index * 0.1}s`,
                  padding: '16px',
                  borderRadius: 'var(--radius-lg)',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '12px',
                  cursor: 'pointer'
                }}
              >
                <div style={{ 
                  fontSize: '20px',
                  width: '32px',
                  height: '32px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  background: 'var(--glass-bg)',
                  borderRadius: '50%'
                }}>
                  {activity.icon}
                </div>
                
                <div style={{ flex: 1, minWidth: 0 }}>
                  <div style={{ 
                    fontWeight: '600', 
                    color: 'var(--text-primary)',
                    marginBottom: '4px',
                    fontSize: '14px'
                  }}>
                    {activity.action}
                  </div>
                  <div style={{ 
                    fontSize: '12px', 
                    color: 'var(--text-secondary)'
                  }}>
                    {activity.venue} ‚Ä¢ {activity.time}
                  </div>
                </div>
                
                <div style={{ 
                  fontWeight: '700',
                  color: activity.amount.startsWith('+') ? 'var(--success)' : 'var(--accent-primary)',
                  fontSize: '16px'
                }}>
                  {activity.amount}
                </div>
              </div>
            ))}
          </div>
        </div>

        <ModernNavbar />

        {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ —Å–∫–∞–Ω–µ—Ä–∞ QR */}
        <QRScannerModal
          isOpen={isQRScannerOpen}
          onClose={() => setIsQRScannerOpen(false)}
          onScan={handleQRScan}
        />
      </div>
    </ErrorBoundary>
  );
};

export default ModernHomePage;
