import React, { useState, useEffect } from 'react';
import { useApp } from '../contexts/AppContext';
import ModernNavbar from './ModernNavbar';
import { ErrorBoundary } from './common/ErrorBoundary';
import ReferralModal from './ReferralModal';

const ProfilePage: React.FC = () => {
  const { user, isAuthenticated, logout } = useApp();
  const [isLoading, setIsLoading] = useState(false);

  const handleLogout = async () => {
    setIsLoading(true);
    try {
      await logout();
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –≤—ã—Ö–æ–¥–∞:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const stats = [
    {
      label: '–í—Å–µ–≥–æ –±–∞–ª–ª–æ–≤',
      value: user?.points || 0,
      icon: '‚≠ê',
      color: 'var(--accent-primary)'
    },
    {
      label: '–ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–æ –∫–æ–¥–æ–≤',
      value: user?.codesUsed || 0,
      icon: 'üé´',
      color: 'var(--success)'
    },
    {
      label: '–ü–æ—Å–µ—â–µ–Ω–∏–π',
      value: user?.visits || 0,
      icon: 'üè™',
      color: 'var(--info)'
    }
  ];

  return (
    <ErrorBoundary>
      <div className="container page-container" style={{ height: '100%', padding: '0 16px' }}>
        {/* –ê–≤–∞—Ç–∞—Ä –∏ –æ–±—â–∏–µ –±–∞–ª–ª—ã */}
        <div className="modern-card fade-in scale-in" style={{ marginBottom: '24px', textAlign: 'center', animationDelay: '0.1s' }}>
          <div 
            className="avatar-animated"
            style={{
              width: '80px',
              height: '80px',
              borderRadius: '50%',
              background: 'var(--accent-gradient)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '32px',
              margin: '0 auto 20px',
              boxShadow: 'var(--shadow-glow)',
              cursor: 'pointer',
              fontWeight: '700',
              color: 'var(--bg-primary)'
            }}
          >
            L
          </div>
          
          <div 
            className="liquid-glass-hover scale-in"
            style={{
              background: 'var(--glass-bg)',
              backdropFilter: 'blur(20px)',
              border: '1px solid var(--glass-border)',
              borderRadius: 'var(--radius-lg)',
              padding: '20px',
              marginBottom: '20px',
              boxShadow: 'var(--shadow-lg)',
              animationDelay: '0.2s',
              cursor: 'pointer',
              transition: 'all var(--transition-normal)'
            }}
          >
            <h2 style={{ margin: '0 0 8px 0', color: 'var(--text-primary)', fontSize: '18px' }}>
              –û–±—â–∏–µ –±–∞–ª–ª—ã
            </h2>
            <div 
              className="pulse"
              style={{ 
                fontSize: '32px', 
                fontWeight: '700', 
                color: 'var(--accent-primary)',
                marginBottom: '4px',
                textShadow: '0 0 20px rgba(100, 216, 203, 0.5)',
                animation: 'pulse 2s ease-in-out infinite'
              }}
            >
              0
            </div>
            <p style={{ margin: '0', color: 'var(--text-secondary)', fontSize: '14px' }}>
              –î–æ—Å—Ç—É–ø–Ω–æ –¥–ª—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è
            </p>
          </div>
        </div>

        {/* –†–∞–∑–¥–µ–ª–∏—Ç–µ–ª—å */}
        <div style={{ 
          height: '1px', 
          background: 'var(--glass-border)', 
          margin: '0 0 24px 0' 
        }}></div>

        {/* –ú–µ–Ω—é –Ω–∞–≤–∏–≥–∞—Ü–∏–∏ */}
        <div className="modern-card fade-in slide-up" style={{ animationDelay: '0.3s' }}>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
            <button 
              className="modern-list-item liquid-glass-hover scale-in"
              style={{ 
                background: 'var(--glass-bg)',
                backdropFilter: 'blur(20px)',
                border: '1px solid var(--glass-border)',
                borderRadius: 'var(--radius-lg)',
                padding: '16px',
                cursor: 'pointer',
                transition: 'all var(--transition-normal)',
                textAlign: 'left',
                width: '100%',
                boxShadow: 'var(--shadow-md)',
                animationDelay: '0.4s'
              }}
              onClick={() => {/* –õ–æ–≥–∏–∫–∞ —Ä–µ—Ñ–µ—Ä–∞–ª—å–Ω–æ–π –ø—Ä–æ–≥—Ä–∞–º–º—ã */}}
            >
              <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                <div 
                  className="bounce"
                  style={{ 
                    fontSize: '20px',
                    animation: 'bounce 2s ease-in-out infinite',
                    animationDelay: '0.1s',
                    fontWeight: '700',
                    color: 'var(--accent-primary)'
                  }}
                >R</div>
                <span style={{ color: 'var(--text-primary)', fontWeight: '500' }}>
                  –†–µ—Ñ–µ—Ä–∞–ª—å–Ω–∞—è –ø—Ä–æ–≥—Ä–∞–º–º–∞
                </span>
              </div>
            </button>

            <button 
              className="modern-list-item liquid-glass-hover scale-in"
              style={{ 
                background: 'var(--glass-bg)',
                backdropFilter: 'blur(20px)',
                border: '1px solid var(--glass-border)',
                borderRadius: 'var(--radius-lg)',
                padding: '16px',
                cursor: 'pointer',
                transition: 'all var(--transition-normal)',
                textAlign: 'left',
                width: '100%',
                boxShadow: 'var(--shadow-md)',
                animationDelay: '0.5s'
              }}
              onClick={() => {/* –õ–æ–≥–∏–∫–∞ –ø–æ–¥–¥–µ—Ä–∂–∫–∏ */}}
            >
              <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                <div 
                  className="pulse"
                  style={{ 
                    fontSize: '20px',
                    animation: 'pulse 1.5s ease-in-out infinite',
                    animationDelay: '0.2s',
                    fontWeight: '700',
                    color: 'var(--accent-secondary)'
                  }}
                >S</div>
                <span style={{ color: 'var(--text-primary)', fontWeight: '500' }}>
                  –ü–æ–¥–¥–µ—Ä–∂–∫–∞
                </span>
              </div>
            </button>

            <button 
              className="modern-list-item liquid-glass-hover scale-in"
              style={{ 
                background: 'var(--glass-bg)',
                backdropFilter: 'blur(20px)',
                border: '1px solid var(--glass-border)',
                borderRadius: 'var(--radius-lg)',
                padding: '16px',
                cursor: 'pointer',
                transition: 'all var(--transition-normal)',
                textAlign: 'left',
                width: '100%',
                boxShadow: 'var(--shadow-md)',
                animationDelay: '0.6s'
              }}
              onClick={() => {/* –õ–æ–≥–∏–∫–∞ –æ –ø—Ä–æ–≥—Ä–∞–º–º–µ */}}
            >
              <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                <div 
                  className="glow"
                  style={{ 
                    fontSize: '20px',
                    animation: 'glow 3s ease-in-out infinite',
                    animationDelay: '0.3s',
                    fontWeight: '700',
                    color: 'var(--text-primary)'
                  }}
                >I</div>
                <span style={{ color: 'var(--text-primary)', fontWeight: '500' }}>
                  –û –ø—Ä–æ–≥—Ä–∞–º–º–µ –ª–æ—è–ª—å–Ω–æ—Å—Ç–∏
                </span>
              </div>
            </button>
          </div>
        </div>

        {/* –ö–Ω–æ–ø–∫–∞ –≤—ã—Ö–æ–¥–∞ */}
        <div className="modern-card fade-in" style={{ animationDelay: '0.6s', textAlign: 'center', marginTop: '32px' }}>
          <button 
            className="modern-btn"
            onClick={handleLogout}
            disabled={isLoading}
            style={{ 
              background: 'var(--error)',
              color: 'white',
              width: '100%',
              opacity: isLoading ? 0.7 : 1
            }}
          >
            {isLoading ? '–í—ã—Ö–æ–¥...' : 'üö™ –í—ã–π—Ç–∏ –∏–∑ –∞–∫–∫–∞—É–Ω—Ç–∞'}
          </button>
        </div>

        <ModernNavbar />
      </div>
    </ErrorBoundary>
  );
};

export default ProfilePage;
