import React, { useState } from 'react';
import { useApp } from '../contexts/AppContext';
import { apiClient } from '../services/apiClient';

interface RegistrationFormProps {
  onComplete: () => void;
}

const RegistrationForm: React.FC<RegistrationFormProps> = ({ onComplete }) => {
  const { user, refetch, hapticFeedback } = useApp();
  const [formData, setFormData] = useState({
    firstName: user?.firstName || user?.first_name || '',
    birthDate: '',
    city: ''
  });
  const [errors, setErrors] = useState<{ [key: string]: string }>({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const validate = () => {
    const newErrors: { [key: string]: string } = {};

    if (!formData.firstName.trim()) {
      newErrors.firstName = '–í–≤–µ–¥–∏—Ç–µ –∏–º—è';
    }

    if (!formData.birthDate) {
      newErrors.birthDate = '–í—ã–±–µ—Ä–∏—Ç–µ –¥–∞—Ç—É —Ä–æ–∂–¥–µ–Ω–∏—è';
    } else {
      const birthDate = new Date(formData.birthDate);
      const today = new Date();
      const age = today.getFullYear() - birthDate.getFullYear();
      const monthDiff = today.getMonth() - birthDate.getMonth();
      
      if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
        // –ï—Å–ª–∏ –¥–µ–Ω—å —Ä–æ–∂–¥–µ–Ω–∏—è –µ—â–µ –Ω–µ –Ω–∞—Å—Ç—É–ø–∏–ª –≤ —ç—Ç–æ–º –≥–æ–¥—É
        if (age < 13 || age > 120) {
          newErrors.birthDate = '–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—É—é –¥–∞—Ç—É —Ä–æ–∂–¥–µ–Ω–∏—è';
        }
      } else if (age < 13 || age > 120) {
        newErrors.birthDate = '–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—É—é –¥–∞—Ç—É —Ä–æ–∂–¥–µ–Ω–∏—è';
      }
    }

    if (!formData.city.trim()) {
      newErrors.city = '–í–≤–µ–¥–∏—Ç–µ –≥–æ—Ä–æ–¥';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!validate()) {
      hapticFeedback.notification('error');
      return;
    }

    setIsSubmitting(true);
    hapticFeedback.selection();

    try {
      const response = await apiClient.put('/user/complete-registration', {
        firstName: formData.firstName.trim(),
        birthDate: formData.birthDate,
        city: formData.city.trim()
      });

      if (response.success) {
        hapticFeedback.notification('success');
        await refetch();
        onComplete();
      } else {
        throw new Error(response.message || '–û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö');
      }
    } catch (error: any) {
      console.error('–û—à–∏–±–∫–∞ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏:', error);
      hapticFeedback.notification('error');
      setErrors({
        submit: error.message || '–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å –¥–∞–Ω–Ω—ã–µ. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.'
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChange = (field: keyof typeof formData, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[field];
        return newErrors;
      });
    }
  };

  // –ü–æ–ª—É—á–∞–µ–º –º–∞–∫—Å–∏–º–∞–ª—å–Ω—É—é –¥–∞—Ç—É (—Å–µ–≥–æ–¥–Ω—è) –∏ –º–∏–Ω–∏–º–∞–ª—å–Ω—É—é (120 –ª–µ—Ç –Ω–∞–∑–∞–¥)
  const today = new Date();
  const maxDate = today.toISOString().split('T')[0];
  const minDate = new Date(today.getFullYear() - 120, 0, 1).toISOString().split('T')[0];

  return (
    <div style={{
      minHeight: '100vh',
      background: 'var(--bg-primary)',
      padding: '20px',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center'
    }}>
      <div className="modern-card" style={{
        maxWidth: '500px',
        width: '100%',
        padding: '32px',
        animation: 'fade-in 0.5s ease'
      }}>
        <div style={{
          textAlign: 'center',
          marginBottom: '32px'
        }}>
          <h1 style={{
            margin: '0 0 12px 0',
            color: 'var(--text-primary)',
            fontSize: '28px',
            fontWeight: '700'
          }}>
            –î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å! üëã
          </h1>
          <p style={{
            margin: '0',
            color: 'var(--text-secondary)',
            fontSize: '16px'
          }}>
            –ó–∞–≤–µ—Ä—à–∏—Ç–µ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—é, —á—Ç–æ–±—ã –Ω–∞—á–∞—Ç—å –∑–∞—Ä–∞–±–∞—Ç—ã–≤–∞—Ç—å –±–∞–ª–ª—ã
          </p>
        </div>

        <form onSubmit={handleSubmit}>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
            {/* –ò–º—è */}
            <div>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: 'var(--text-primary)',
                fontSize: '14px',
                fontWeight: '600'
              }}>
                –ò–º—è *
              </label>
              <input
                type="text"
                value={formData.firstName}
                onChange={(e) => handleChange('firstName' as keyof typeof formData, e.target.value)}
                className="modern-input"
                placeholder="–í–≤–µ–¥–∏—Ç–µ –≤–∞—à–µ –∏–º—è"
                style={{
                  width: '100%',
                  padding: '14px',
                  background: 'var(--glass-bg)',
                  border: errors.firstName 
                    ? '1px solid var(--error)' 
                    : '1px solid var(--glass-border)',
                  borderRadius: 'var(--radius-lg)',
                  color: 'var(--text-primary)',
                  fontSize: '16px'
                }}
                disabled={isSubmitting}
              />
              {errors.firstName && (
                <div style={{
                  marginTop: '6px',
                  color: 'var(--error)',
                  fontSize: '12px'
                }}>
                  {errors.firstName}
                </div>
              )}
            </div>

            {/* –î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è */}
            <div>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: 'var(--text-primary)',
                fontSize: '14px',
                fontWeight: '600'
              }}>
                –î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è *
              </label>
              <input
                type="date"
                value={formData.birthDate}
                onChange={(e) => handleChange('birthDate' as keyof typeof formData, e.target.value)}
                min={minDate}
                max={maxDate}
                className="modern-input"
                style={{
                  width: '100%',
                  padding: '14px',
                  background: 'var(--glass-bg)',
                  border: errors.birthDate 
                    ? '1px solid var(--error)' 
                    : '1px solid var(--glass-border)',
                  borderRadius: 'var(--radius-lg)',
                  color: 'var(--text-primary)',
                  fontSize: '16px'
                }}
                disabled={isSubmitting}
              />
              {errors.birthDate && (
                <div style={{
                  marginTop: '6px',
                  color: 'var(--error)',
                  fontSize: '12px'
                }}>
                  {errors.birthDate}
                </div>
              )}
            </div>

            {/* –ì–æ—Ä–æ–¥ */}
            <div>
              <label style={{
                display: 'block',
                marginBottom: '8px',
                color: 'var(--text-primary)',
                fontSize: '14px',
                fontWeight: '600'
              }}>
                –ì–æ—Ä–æ–¥ *
              </label>
              <input
                type="text"
                value={formData.city}
                onChange={(e) => handleChange('city' as keyof typeof formData, e.target.value)}
                className="modern-input"
                placeholder="–í–≤–µ–¥–∏—Ç–µ –≤–∞—à –≥–æ—Ä–æ–¥"
                style={{
                  width: '100%',
                  padding: '14px',
                  background: 'var(--glass-bg)',
                  border: errors.city 
                    ? '1px solid var(--error)' 
                    : '1px solid var(--glass-border)',
                  borderRadius: 'var(--radius-lg)',
                  color: 'var(--text-primary)',
                  fontSize: '16px'
                }}
                disabled={isSubmitting}
              />
              {errors.city && (
                <div style={{
                  marginTop: '6px',
                  color: 'var(--error)',
                  fontSize: '12px'
                }}>
                  {errors.city}
                </div>
              )}
            </div>

            {/* –û–±—â–∞—è –æ—à–∏–±–∫–∞ */}
            {errors.submit && (
              <div style={{
                padding: '12px',
                background: 'rgba(239, 68, 68, 0.1)',
                border: '1px solid rgba(239, 68, 68, 0.3)',
                borderRadius: 'var(--radius-md)',
                color: 'var(--error)',
                fontSize: '14px',
                textAlign: 'center'
              }}>
                {errors.submit}
              </div>
            )}

            {/* –ö–Ω–æ–ø–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏ */}
            <button
              type="submit"
              className="modern-btn"
              disabled={isSubmitting}
              style={{
                width: '100%',
                padding: '16px',
                marginTop: '8px',
                opacity: isSubmitting ? 0.7 : 1,
                cursor: isSubmitting ? 'not-allowed' : 'pointer'
              }}
            >
              {isSubmitting ? '–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ...' : '–ó–∞–≤–µ—Ä—à–∏—Ç—å —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—é'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default RegistrationForm;
